import React from 'react';
import { useState } from "react";
import trainData from '../server/trainData';

export default function Train(props) {
    const { trainData } = props;

    const onTime = "On time";
    if (trainData.DELAY !== "T0S") {
        onTime = "DELAYED";
    }

    function lowerCase(station) {
        const name = station.charAt(0) + station.slice(1,station.length).toLowerCase()
        const newName = name.map((char) => {
            if (char === " ") {
                const i = name.indexOf(" ");
                name[i].toUpperCase();
                return newName;
            }
        }); 
        return name;
    }

    return (
        <div className="all">
            <div id="bothSides">
                <img id="icon" src="/images/capitalM"></img>
                <div id="allInfo">
                    <div id="info">
                        <div id="location">
                            <div id="station1">{trainData.STATION.lowerCase()}</div>
                            <img id="arrow" src="/images/arrow.jpg"></img>
                            <div id="station2">{trainData.DESTINATION.lowerCase()}</div>
                        </div>
                        <div id="bottomInfo">
                            <div id="lineColor">{trainData.LINE.lowerCase()}</div>
                            <p id="onTime">{}</p>
                        </div>
                    </div>
                    <div id="time">
                        <p id="num">{trainData.WAITING_TIME.split(" ")[0]}</p>
                        <p id="mins">{trainData.WAITING_TIME.split(" ")[1]}</p>
                    </div>
                </div>
            </div>
        </div>
    );
}
