import React, { useState } from 'react';
import Train from '../components/Train';
import "./TrainList.css";

export default function TrainList(props) {
    const { data, currColor, currStation, isArrClicked, isSchedClicked, isBound1Clicked, isBound2Clicked } = props;
    const filtered = data.filter((train) => {
        const color = train.LINE === currColor;
        const arr = train.WAITING_TIME === "Arriving";
        const sched = train.WAITING_TIME !== "Arriving";
        const direction = (currColor === "GREEN" || currColor === "BLUE");
        const north = train.DIRECTION === "N";
        const south = train.DIRECTION === "S";
        const west = train.DIRECTION === "W";
        const east = train.DIRECTION === "E";
        console.log(train);

        return (
            color && ((isArrClicked && arr) && (isSchedClicked && sched) && 
            (direction ? (isBound1Clicked && east) || (isBound2Clicked && west) : (isBound1Clicked && north) || (isBound2Clicked && south)))
        );
      });
    console.log("hi");
    console.log(filtered);
    return (
        <div className="allTrains">{filtered?.map((train) => (<Train trainData={train} />))}</div>
    );
}
