// get static data
import React from 'react';
import { useState, useEffect } from 'react';
import NavBar from '../components/NavBar';
import TrainList from '../components/TrainList';
import './LinesPage.css';

export default function LinesPage() {
  const [currColor, setCurrColor] = useState("GOLD");
  const [loading, setLoading] = useState(true);
  const [trainData, setTrainData] = useState(null);
  const [stationData, setStationData] = useState(null);

  const APITrainURL = `http://13.59.196.129:3001/arrivals/`;
  const APIStationURL = `http://13.59.196.129:3001/stations/`;

  useEffect(() => {
    async function fetchTrainData() {
      setLoading(true);
      const result = await fetch(APITrainURL + currColor.toLowerCase());
      const fetchedData = await result.json();
      setTrainData(fetchedData);
    }
    setTimeout(() => {
      fetchTrainData();
    }, 200)

    setLoading(false);
  },[currColor])

  useEffect(() => {
    async function fetchStationData() {
      setLoading(true);
      const result = await fetch(APIStationURL + currColor.toLowerCase());
      const fetchedData = await result.json();
      setStationData(fetchedData);
    }
    fetchStationData();
    setLoading(false);
  },[currColor])

  return (
    <div className="lines">
        <div id="lineButtons">
          <button onClick={((e) => {
            if (currColor !== "GOLD") {
              setCurrColor("GOLD")
              setLoading(true)
            }
            })} id="gold">Gold</button>
          <button onClick={((e) => {
            if (currColor !== "RED") {
              setCurrColor("RED")
              setLoading(true)
            }
            })} id="red">Red</button>
          <button onClick={((e) => {
            if (currColor !== "BLUE") {
              setCurrColor("BLUE")
              setLoading(true)
            }
            })} id="blue">Blue</button>
          <button onClick={((e) => {
            if (currColor !== "GREEN") {
              setCurrColor("GREEN")
              setLoading(true)
            }
            })} id="green">Green</button>
          <div id="bound"></div>
        </div>
        <div id="lineName">{currColor}</div>
        <div id="boundary"></div>
        <div id="bothSides">
            <div id="nav"><NavBar color={currColor} data={stationData} /></div>
            <div id="rightSide">
            {loading ? (<div>Loading...</div>) : (
                <><div id="nonfuncButtons">
              <button id="arriving">Arriving</button>
              <button id="sched">Scheduled</button>
              <button id="bound1">{(currColor === "GREEN" || currColor === "BLUE") ? 'Eastbound' : 'Northbound'}</button>
              <button id="bound2">{(currColor === "GREEN" || currColor === "BLUE") ? 'Westbound' : 'Southbound'}</button>
            </div><TrainList data={trainData} /></>)
            }
            </div>
        </div>
    </div>
  );
}
