// get static data
import React from 'react';
import { useState, useEffect } from 'react';
import NavBar from '../components/NavBar';
import TrainList from '../components/TrainList';
import './LinesPage.css';

export default function LinesPage() {
  const [currColor, setCurrColor] = useState("GOLD");
  const [loading, setLoading] = useState(true);
  const [trainData, setTrainData] = useState(null);
  const [stationData, setStationData] = useState(null);
  const [isArrClicked, setIsArrClicked] = useState(false);
  const [isSchedClicked, setIsSchedClicked] = useState(false);
  const [isBound1Clicked, setIsBound1Clicked] = useState(false);
  const [isBound2Clicked, setIsBound2Clicked] = useState(false);

  let filtered;

  const APITrainURL = `http://13.59.196.129:3001/arrivals/`;
  const APIStationURL = `http://13.59.196.129:3001/stations/`;

  useEffect(() => {
    async function fetchTrainData() {
      setLoading(true);
      const result = await fetch(APITrainURL + currColor.toLowerCase());
      const fetchedData = await result.json();
      setTrainData(fetchedData);
      setLoading(false);
    }
    setTimeout(() => {
      fetchTrainData();
    }, 100)
  },[currColor])

  useEffect(() => {
    async function fetchStationData() {
      setLoading(true);
      const result = await fetch(APIStationURL + currColor.toLowerCase());
      const fetchedData = await result.json();
      setStationData(fetchedData);
    }
    setTimeout(() => {
      fetchStationData();
    }, 100)
  },[currColor])

  const buttonStyle = {
    width: "150px",
    height: "40px",
    bordeRadius: "10px",
    textAlign: "center",
    lineHeight: "4px",
    fontSize: "20px",
    backgroundColor: "rgb(37, 36, 36)",
    color: "white",
    border: "none",
    marginRight: "60px",
    fontFamily: "'Segoe UI', Tahoma, Geneva, Verdana, sans-serif",
  }

  return (
    <div className="lines">
        <div id="lineButtons">
          <button onClick={((e) => {
            if (currColor !== "GOLD") {
              setCurrColor("GOLD")
              setLoading(true)
            }
            })} id="gold">Gold</button>
          <button onClick={((e) => {
            if (currColor !== "RED") {
              setCurrColor("RED")
              setLoading(true)
            }
            })} id="red">Red</button>
          <button onClick={((e) => {
            if (currColor !== "BLUE") {
              setCurrColor("BLUE")
              setLoading(true)
            }
            })} id="blue">Blue</button>
          <button onClick={((e) => {
            if (currColor !== "GREEN") {
              setCurrColor("GREEN")
              setLoading(true)
            }
            })} id="green">Green</button>
          <div id="bound"></div>
        </div>
        <div id="lineName">{currColor}</div>
        <div id="boundary"></div>
        <div id="bothSides">
            <div id="nav"><NavBar color={currColor} data={stationData} /></div>
            <div id="rightSide">
            {loading ? (<div id="loading">Loading...</div>) : (
                <><div id="funcButtons">
              <button onClick={(e) => {
                { 
                  setIsArrClicked(true)
                  setIsBound1Clicked(false)
                  setIsBound2Clicked(false)
                  setIsSchedClicked(false)
                }
                filtered = trainData.filter((train) => train.WAITING_TIME === "Arriving")
              }} id={isArrClicked ? "clicked" : "normal"}>Arriving</button>
              <button onClick={(e) => {
                {
                  setIsSchedClicked(true)
                  setIsArrClicked(false)
                  setIsBound1Clicked(false)
                  setIsBound2Clicked(false)
                }
                filtered = trainData.filter((train) => train.WAITING_TIME !== "Arriving")
              }} id={isSchedClicked ? "clicked" : "normal"}>Scheduled</button>
              <button onClick={(e) => {
                {
                  setIsBound1Clicked(true)
                  setIsArrClicked(false)
                  setIsBound2Clicked(false)
                  setIsSchedClicked(false)
                }
                {(currColor === "GREEN" || currColor === "BLUE") ? filtered = trainData.filter((train) => train.DIRECTION === "E") : filtered = trainData.filter((train) => train.DIRECTION === "N")}
              }} id={isBound1Clicked ? "clicked" : "normal"}>{(currColor === "GREEN" || currColor === "BLUE") ? 'Eastbound' : 'Northbound'}</button>
              <button onClick={(e) => {
                {
                  setIsBound2Clicked(true)
                  setIsArrClicked(false)
                  setIsBound1Clicked(false)
                  setIsSchedClicked(false)
                }
                {(currColor === "GREEN" || currColor === "BLUE") ? filtered = trainData.filter((train) => train.DIRECTION === "W") : filtered = trainData.filter((train) => train.DIRECTION === "S")}
              }} id={isBound2Clicked ? "clicked" : "normal"}>{(currColor === "GREEN" || currColor === "BLUE") ? 'Westbound' : 'Southbound'}</button>
            </div><TrainList data={filtered} /></>)
            }
            </div>
        </div>
    </div>
  );
}
